  $(document).ready(function () {

    /* Magnific Popup modal window */
    $('.popup-with-zoom-anim').magnificPopup({
      type: 'inline',
      fixedContentPos: true,
      fixedBgPos: true,
      overflowY: 'hidden',
      closeBtnInside: true,
      preloader: false,
      midClick: true,
      removalDelay: 300,
      mainClass: 'my-mfp-zoom-in'
    });

  });/*document ready end*/
